glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
in vec2 a_uv0;
out vec2 v_uv0;
void main () {
  vec4 pos = vec4(a_position, 1);
  pos = cc_matViewProj * pos;
  v_uv0 = a_uv0;
  v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
#if USE_ALPHA_TEST
  uniform ALPHA_TEST {
    float alphaThreshold;
  };
#endif
void ALPHA_TEST (in vec4 color) {
  #if USE_ALPHA_TEST
      if (color.a < alphaThreshold) discard;
  #endif
}
void ALPHA_TEST (in float alpha) {
  #if USE_ALPHA_TEST
      if (alpha < alphaThreshold) discard;
  #endif
}
in vec4 v_color;
in vec2 v_uv0;
uniform sampler2D texture;
uniform POST {
  float num;
};
vec4 draw(vec2 uv) {
  return v_color * texture2D(texture,uv).rgba;
}
float rand(vec2 co){
  return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}
void main () {
  vec4 blurred = vec4(0.);
  for (float i = 0.; i < 5.; i++) {
      vec2 q = vec2(cos(degrees((i/5.)*360.)),sin(degrees((i/5.)*360.))) * (rand(vec2(i,v_uv0.x+v_uv0.y))+num);
      vec2 uv2 = v_uv0+(q*num);
      blurred += draw(uv2)/2.;
      q = vec2(cos(degrees((i/5.)*360.)),sin(degrees((i/5.)*360.))) * (rand(vec2(i+2.,v_uv0.x+v_uv0.y+24.))+num);
      uv2 = v_uv0+(q*num);
      blurred += draw(uv2)/2.;
  }
  blurred /= 5.;
  vec4 col = vec4(blurred);
  ALPHA_TEST(col);
  gl_FragColor = col;
}

