glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
in vec2 a_uv0;
out vec2 v_uv0;
void main () {
  vec4 pos = vec4(a_position, 1);
  pos = cc_matViewProj * pos;
  v_uv0 = a_uv0;
  v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
in vec4 v_color;
in vec2 v_uv0;
uniform sampler2D texture;
uniform TIME {
  float time;
};
void main () {
  float time = time * .5+5.;
  vec2 uv = v_uv0.xy;
  vec2 p = mod(uv*6.12, 6.12)-250.0;
  vec2 i = vec2(p);
  float c = 0.8;
  float inten = .0045;
  for (int n = 0; n < 5; n++)
  {
      float t =  time * (1.0 - (3.5 / float(n+1)));
      i = p + vec2(cos(t - i.x) + sin(t + i.y), sin(t - i.y) + cos(1.5*t + i.x));
      c += 1.0/length(vec2(p.x / (cos(i.x+t)/inten),p.y / (cos(i.y+t)/inten)));
  }
  c /= float(5);
  c = 1.17-pow(c, 1.4);
  vec4 tex = texture2D(texture,uv);
  vec3 color = vec3(pow(abs(c), 20.0));
  color = clamp(color + vec3(0.0, 0.0, .0), 0.0, tex.a);
  float alpha = c*tex[3];
  tex[0] = tex[0] + color[0]*alpha;
  tex[1] = tex[1] + color[1]*alpha;
  tex[2] = tex[2] + color[2]*alpha;
  gl_FragColor = v_color * tex;
}

