glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
in vec2 a_uv0;
out vec2 v_uv0;
void main () {
  vec4 pos = vec4(a_position, 1);
  pos = cc_matViewProj * pos;
  v_uv0 = a_uv0;
  v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
in vec4 v_color;
in vec2 v_uv0;
uniform LIGHT {
  vec2 mouse;
};
void main () {
  float radius = 0.3;
  float distance  = length( mouse - v_uv0 );
  float intensity = min( distance, radius )/radius;
  gl_FragColor = vec4(.0,.0,.0,1.) * vec4(intensity);
}

glsl1.vert:
precision highp float;
uniform mat4 cc_matViewProj;
attribute vec3 a_position;
attribute vec4 a_color;
varying vec4 v_color;
attribute vec2 a_uv0;
varying vec2 v_uv0;
void main () {
  vec4 pos = vec4(a_position, 1);
  pos = cc_matViewProj * pos;
  v_uv0 = a_uv0;
  v_color = a_color;
  gl_Position = pos;
}

glsl1.frag:
precision highp float;
varying vec4 v_color;
varying vec2 v_uv0;
uniform vec2 mouse;
void main () {
  float radius = 0.3;
  float distance  = length( mouse - v_uv0 );
  float intensity = min( distance, radius )/radius;
  gl_FragColor = vec4(.0,.0,.0,1.) * vec4(intensity);
}
