glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
#if USE_TEXTURE
in vec2 a_uv0;
out vec2 v_uv0;
#endif
void main () {
  vec4 pos = vec4(a_position, 1);
  #if CC_USE_MODEL
  pos = cc_matViewProj * cc_matWorld * pos;
  #else
  pos = cc_matViewProj * pos;
  #endif
  #if USE_TEXTURE
  v_uv0 = a_uv0;
  #endif
  v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
#if USE_ALPHA_TEST
  uniform ALPHA_TEST {
    float alphaThreshold;
  };
#endif
#if USE_TEXTURE
in vec2 v_uv0;
uniform sampler2D texture;
#endif
uniform sampler2D mixtexture;
uniform POST{
  vec2 pos;
};
float getArea(vec2 a, vec2 b, vec2 c){
  return abs(a.x * b.y - a.x * c.y + b.x * c.y - b.x * a.y + c.x * a.y - c.x * b.y) * 0.5;
}
void main () {
  vec2 postemp = pos;
  postemp.x += 0.3;
  vec4 o = texture2D(texture, v_uv0);
  float r = distance(v_uv0.xy, postemp);
  o.a *= smoothstep(0., 0.1, r);
  o.a *= smoothstep(0., 0.1, r);
  o.a *= smoothstep(0., 0.1, r);
  vec2 x1 = pos;
  vec2 x2 = postemp + vec2(0., 0.09);
  vec2 x3 = postemp - vec2(0., 0.09);;
  float temp = getArea(x1, x2, vec2(v_uv0.xy)) + getArea(x2, x3, vec2(v_uv0.xy))
    + getArea(x1, x3, vec2(v_uv0.xy));
  float s = getArea(x1, x2, x3);
  if(clamp(temp - s, 0.0, 1.0) < 0.002){
    o.a *= smoothstep(0., 0.3, 0.3 - abs(v_uv0.x - postemp.x));
  }
  else{
  }
  gl_FragColor = o;
}

